            //going in the left direction again to see if path is possible in the left direction
            //ans=1 because we already started our path 
            solve(root->left,1,1);
        }
    }
    int longestZigZag(TreeNode* root) {
        //condition to go in left part
        solve(root,0,0);
        //condition to go in right part
        solve(root,1,0);
        return max_ans;
    }
};
