    //0->left,1->right,2->initial node ,both ways possible

    map< pair<TreeNode*,int>,int>mp; 
    int recurs(TreeNode* root,int direction)
    {
        if(root==NULL)
        {
            return 0;
        }

        if(mp[{root,direction}]!=0)
        {
                return mp[{root,direction}];
        }

        if(direction==0)
        {
            return mp[{root,direction}]= recurs(root->right,1)+1;
        }
        else if(direction==1)
        {
             return mp[{root,direction}]=recurs(root->left,0)+1;
        }
        else
        {
            return mp[{root,direction}]=max(recurs(root->right,1)+1,recurs(root->left,0)+1);
        }
    }


    int ans=0;
    void pre(TreeNode* root)
    {
        if(root==NULL)
        {
            return ;
        }

        ans=max(recurs(root,2),ans);
        pre(root->left);
        pre(root->right);
    }



    int longestZigZag(TreeNode* root) {
        pre(root);
        return ans-1;
    }
};
