class Solution:
    def longestZigZag(self, root: Optional[TreeNode]) -> int:
        q= deque()
        q.append([root,'',0])
        ans=0
        while(q):
            u,prev_dir,cnt=q.popleft()
            ans= max(ans,cnt)
            if(u.left):
                if(prev_dir=='r'):
                    q.append([u.left,'l',cnt+1])
                else:
                    q.append([u.left,'l',1])
            if(u.right):
                if(prev_dir=='l'):
                    q.append([u.right,'r',cnt+1])
                else:
                    q.append([u.right,'r',1])
        return ans
