class Solution {

    int ans = 0;    //global variable ans to store and compare longest path

    public int longestZigZag(TreeNode root) {

		//to keep a record of previous traversal boolean flag is used 
        pathFinder(root.left,true,0);       //from root to left and then to right so flag is true
        pathFinder(root.right,false,0);     //from root to right and then  to left so flag is false
        return ans;
    }

    public void pathFinder(TreeNode root, boolean flag, int sum){

		//base condition
        if(root==null){
            ans = Math.max(ans,sum);
            return;
        }

		//true means we have to go right.
        if(flag){
            pathFinder(root.right,false,sum+1);  //recursive call for root.right
            pathFinder(root.left,true,0);        //but there can be new ZigZag path from root.left
        }
        else{
			//we have to go left
            pathFinder(root.left,true,sum+1);  //recursive call for root.left
            pathFinder(root.right,false,0);    //but there can be new ZigZag path from root.left
        }
    }

}
