class Solution {
   int max = 0;

	public int longestZigZag(TreeNode root) {
		if (root == null)
			return 0;
        // start with depth (indicates count of nodes in zigzag path sofar) zero in both directions
		path(root.left, 0, false);
		path(root.right, 0, true);
        // return max zigzag path found
		return max;
	}

	//using boolean value direction: false-left true-right
	public void path(TreeNode node, int depth, boolean direction) {
        // capture max depth of zigzag path captured sofar in global variable max
		max = Math.max(max, depth);
		if (node == null)
			return;
        // if previous direction is right(i.e direction is true) and we r heading towards left increment depth+1 as it counts towards valid zigzag path
		otherwise its important that we need to reset because current zigzag path breaks here and we need to start again to see if there is any other zigzag path from this node.
		path(node.left, direction ? depth + 1 : 0, false);
        // likewise if previous zigzag path is left(i.e direction is false) and we r heading towards right increment depth+1 is it counts towards valid zigzag path 
		otherwise reset current count sofar and start again from here
		path(node.right, !direction ? depth + 1 : 0, true);
	}
}
