            return Math.max(f(root.left, true, count + 1), Math.max(f(root.right, true, 0),f(root.right, false, 0)));
        }
    }

    public int longestZigZag(TreeNode root) {
        if (root == null) {
            return 0;
        }
        return Math.max(f(root.left, true, 0), f(root.right, false, 0));
    }
}

