/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int count(TreeNode* root,int check,int ans){
        if(!root)
          return ans;
        if(check==0)   //for left child
        {
            int a=0;
            a=count(root->left,1,ans+1);    //zigzag start from any previous node
            a=max(a,count(root->right,0,0)); //new zigzag from current node
            return a;   //maxi. length of zigzag upto current node
        }
        if(check==1)   //for right child
        {
            int b=0;
            b=count(root->right,0,ans+1);    //zigzag start from any previous node
            b=max(b,count(root->left,1,0));  //new zigzag from current node
            return b;   //maxi. length of zigzag upto current node
        }
        return max(count(root->left,1,0),count(root->right,0,0)); //for root( left child and right child)
    }
    int longestZigZag(TreeNode* root) {
       if(!root)
         return 0;
        int ans=count(root,-1,0);
        return ans;
    }
};
```
