```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    // keeps track of no. of nodes in longest zig zag path
    int res = 0;

    // post order manner
    vector<int> traverse(TreeNode *root){
        if(!root) return {0, 0}; // by default NULL node returns path with 0 nodes,

        vector<int> leftDir = traverse(root->left);
        vector<int> rightDir = traverse(root->right);

        // currDir[0] - left side and currDir[1] - right side
        vector<int> currDir = {0, 0};

        // current's node longest zig zag path in 'left' will be 1 + its left child's 'right' path 
        currDir[0] = leftDir[1] + 1;

        // current's node longest zig zag path in 'right' will be 1 + its right child's 'left' path
        currDir[1] = rightDir[0] + 1;

        // updating the answer
        res = max(res, max(currDir[0], currDir[1]));

        return currDir;
    }

    int longestZigZag(TreeNode* root) {
        // traverse the whole tree
        traverse(root);

