        // so left part of our given root will contain 1 + right_subtree.right part
        int left = 1 + rsub.second;

        //if root is left part of parent root
        int right = 1+ lsub.first;

        // max_zig for maintaining maximum zigzag pattern found in entire traversal
        max_zig = max(max_zig,max(left,right));

        return {left,right};
    }

    int longestZigZag(TreeNode* root) {
        if(root==NULL || (root->left==NULL && root->right==NULL)) return 0;
        int max_zig = 0;
        pair<int,int> res = solve(root,max_zig);
        return max_zig-1;
    }
};

