        if (node == null) return 0;
        int left = helper(node.left, isLeft ? 0 : path + 1, !isLeft);
        int right = helper(node.right, isLeft ? path + 1 : 0, !isLeft);
        return Math.max(path, Math.max(left, right));
    }
