        max = length_curr;
    }

    if((root->right == NULL) && (root->left == NULL))
    {
        return;
    }

    if(root->right != NULL)
    {
        longestZigZagRecur(root->right, RIGHT, ((way == LEFT))? length_curr + 1 : 1);
    }

    if(root->left != NULL)
    {
        longestZigZagRecur(root->left, LEFT, ((way == RIGHT))? length_curr + 1 : 1);
    }
}

