class Solution {
    private int max = 0;
    public int longestZigZag(TreeNode root) {
        // we are currently standing on the root node
        dfs(root.left, 1, false);   // call for left
        dfs(root.right, 1, true);   // call for right
        // Why 1? Because from root to root's left or right 
        // is a distance of 1 unit
        return max;
    }

    // the parameter boolean goRight will let us know 
    // if we took the right direction or the left direction
    private void dfs(TreeNode root, int depth, boolean goRight) {
        if (root == null) {
            return;
        }
        // update max distance
        max = Math.max(max, depth); 
        // if we had took right direction
        if (goRight) {
            // we have 2 options:
            // Go left: That'd be a zigzag order so distance + 1
            dfs(root.left, depth + 1, false);
            // Go right: Zigzag order is broken so distance resets to 1
            dfs(root.right, 1, true);
        } 
        // Else if we had took a left direction
        else {
            // we have 2 options:
            // Go left: Zigzag order is broken so distance resets to 1
            dfs(root.left, 1, false);
            // Go right: That'd be a zigzag order so distance + 1
            dfs(root.right, depth + 1, true);
        }
        // In each call we set goRight true or false accordingly 
        // if we are moving to left or right

        // Also, why are we resetting the distance to 1 and not 0?
        // Because, from current node to next node where we are 
        // stepping on, the distance is 1, so we set it to 1.
    }
}
