# Code
```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    void helper(TreeNode* root,int steps,int& maxSum,bool goLeft) {
        if (root == nullptr) {
            return; // Null check to handle base case
        }

        maxSum = max(maxSum,steps);

        if(goLeft == true) {
            helper(root->left,steps+1,maxSum,false);

            helper(root->right,1,maxSum,true);
        }

        else {
            helper(root->left,1,maxSum,false);

            helper(root->right,steps+1,maxSum,true);
        }
    }

    int longestZigZag(TreeNode* root) {
        int maxSum = -1;
        if(root == NULL)
            return 0;

        bool goLeft = true;

        helper(root,0,maxSum,goLeft);

        return maxSum;
    }
};
```

