    void helper(TreeNode* root, bool goLeft, int step){
        if(root==NULL){ return; }
        maxStep= max(maxStep, step);

        if(goLeft==true){ // I want to go left
            helper(root->left, false, step+1); // I went left, so step+1, now in the next step, I want to go right, so goLeft= false 
            helper(root->right, true, 1); // I went right, step reseted
        }
        else if(goLeft==false){ // I want to go right
            helper(root->right, true, step+1); // I went right, so step+1, now In next step, I want to go left, so goLeft= true
            helper(root->left, false, 1); // I went left, step reseted
        }
        return;
    }

    int longestZigZag(TreeNode* root) {
        maxStep=0;
        helper(root, true, 0); // from root, we will move to left, and we have taken 0 steps till now
        helper(root, false, 0); // from root, we will move to right, and we have taken 0 steps till now
        return maxStep;
    }
};
