Intuitive solution which handles all cases well.

```
class Solution {
public:

//ans will store our final ans     
int ans =0;

struct str{
    int stn; // Sum till now of the present node value and it's left and right subtree 
    int max; // Min value in subtree 
    int min; // Max value in subtree 
    int ans; // Sum of largest BST which which is a subtree of the present node 
    bool isBST; // If subtree is BST 
}; 


str util(TreeNode* root){

    //if root is NULL
    if (root == NULL) 
        return {0, INT_MIN, INT_MAX, 0, true}; 

    //if root is a leaf node
    if (root->left == NULL && root->right == NULL) 
        return {root->val, root->val, root->val, root->val, true}; 

    //recurring for the left and right subtrees
    str lt = util(root->left); 
    str rt = util(root->right); 

    //create a structure to return the values for the current node
    str ret_cur; 
    ret_cur.stn = (root->val + lt.stn + rt.stn);

    // If whole tree rooted under current root is BST
    if (lt.isBST && rt.isBST && lt.max < root->val && rt.min > root->val) {

        //update the max and the min for the present node
        ret_cur.min = min(lt.min, root->val); 
        ret_cur.max = max(rt.max, root->val); 

        //update answer for tree rooted under current 'root' 
        ret_cur.ans = ret_cur.stn; 
        ret_cur.isBST = true; 

        //update the overall final ans
        ans = max(ans, ret_cur.ans);

        //return the ret_cur to the calling function
        return ret_cur;

    }

    //if whole tree is not BST, return maximum of left and right subtrees 
    ret_cur.ans = max(lt.ans, rt.ans);

    //update the overall final ans
    ans = max(ret_cur.ans,ans);

    //since the tree rooted with the current node is not a  BST
    ret_cur.isBST = false; 

    //return the ret_cur
    return ret_cur; 
} 



int maxSumBST(TreeNode* root) {
    //return 0 for the base case when the given tree is NULL
    if(root==NULL)
	    return 0;
    return max(ans,util(root).ans);
   }
};
```

