class Solution {
    int res = 0;
    public class Output{
        boolean bst;
        int sum, min, max;
        public Output(boolean bst, int sum, int min, int max) {
            this.bst = bst;
            this.sum = sum;
            this.min = min;
            this.max = max;
        }
    }
    public int maxSumBST(TreeNode root) {
        if (root == null) return 0;
        dfs(root);
        return res;
    }

    private Output dfs(TreeNode node) {
        boolean isBST = false;
        int sum = 0, min = 0, max = 0;
        if (node.left == null && node.right == null) {
            res = Math.max(res, node.val);
            return new Output(true, node.val, node.val, node.val);
        }
        if (node.left == null) {
            Output right = dfs(node.right);
            isBST = right.bst && node.val < right.min;
            if (isBST) res = Math.max(res, node.val + right.sum);
            return new Output(isBST, node.val + right.sum, node.val, right.max);
        }

        if (node.right == null) {
            Output left = dfs(node.left);
            isBST = left.bst && node.val > left.max;
            if (isBST) res = Math.max(res, node.val + left.sum);
            return new Output(isBST, node.val + left.sum, left.min, node.val);
        }

        Output left = dfs(node.left), right = dfs(node.right);
        isBST = left.bst && right.bst && node.val > left.max && node.val < right.min;
        if (isBST) res = Math.max(res, node.val + left.sum + right.sum);
        return new Output(isBST, node.val + left.sum + right.sum, left.min, right.max);

    }
}
