class DataNode{          // To keep track of min max value, sum of subtree below it and if subtree rooted at current node is BST or not.
public:
    int minVal = INT_MAX;
    int maxVal = INT_MIN;
    int sum = 0;
    bool isBST = false;
};

class Solution {
public:

    int maxSum = 0;

    DataNode solve(TreeNode* root)
    {
        if(!root) return {INT_MAX, INT_MIN, 0, true};

        if(!root->left && !root->right)                  // If leaf node then return it as a BST
        {
            maxSum = max(maxSum, root->val);
            return {root->val, root->val, root->val, true};
        }

        DataNode left = solve(root->left);                 // Left node Data
        DataNode right = solve(root->right);           // Right node Data

        int sum = left.sum + right.sum + root->val;    // Sum of subtree rooted at current node
        bool isBST = false;

        if(left.maxVal < root->val && right.minVal > root->val && left.isBST && right.isBST)   // Check if current node is BST or not
        {   
            isBST = true;                // Mark it as true, i.e BST
            maxSum = max(maxSum, sum);       // Also find the maximum sum of subtree
        }

        return {min(root->val, left.minVal), max(root->val, right.maxVal), sum, isBST};
    }

    int maxSumBST(TreeNode* root) {

        solve(root);
        return maxSum;
    }
};
