            curSum=0;
            curMaxSum=max(lp.maxsum,rp.maxsum);
            curMin=INT_MIN;
            curMax=INT_MAX;
        }
        struct pair cur={isBST,curMaxSum,curSum,curMin,curMax};
        return cur;
    }

    int maxSumBST(TreeNode* root) {
        return max(0,solve(root).maxsum);
    }
};
