class Solution:
    def maxSumBST(self, root: TreeNode) -> int:
        self.ans = 0 #empty BST allowed
        def help(r):
            if not r:
                return True,0,math.inf,-math.inf # Note -infinity as upper bound and +infinity as lower bound

            isLeftBST,leftSum,leftMin,leftMax = help(r.left)           
            isRightBST,rightSum,rightMin,rightMax = help(r.right)

