class Solution {
public:

    bool get_output(TreeNode * root, int & sum,int & part_sum, int & min_val, int & max_val){



        if(root->left==NULL and root->right==NULL){
            part_sum = root->val;
            sum = max(sum,root->val);
            min_val = root->val;
            max_val = root->val;
            return true;
        }

        // other condition
        bool left_part = true, right_part= true;

        int left_part_sum=0, right_part_sum=0 ;
        int min_val1=INT_MAX,max_val1=INT_MIN ,min_val2=INT_MAX,max_val2=INT_MIN ;

        if(root->left)
            left_part = get_output(root->left,sum,left_part_sum,min_val1,max_val1);
        if(root->right)
            right_part = get_output(root->right,sum, right_part_sum,min_val2 ,max_val2);

        if(!left_part or !right_part){
            return false;
        }

        if(root->val<=max_val1 or root->val>=min_val2){
            return false;
        }

        min_val = min({root->val,min_val1,min_val2});
        max_val = max({root->val,max_val1,max_val2});        

        // update the condition
        sum = max(sum,left_part_sum+right_part_sum + root->val);

        part_sum = left_part_sum+ right_part_sum + root->val;

        return true;

    }
    int maxSumBST(TreeNode* root) {

        // Base Case
        if(root==NULL){
            return 0;
        }

        int sum =INT_MIN;

        bool isValid= false;


        int part_sum =0;
        int min_val,max_val;
        isValid = get_output(root,sum,part_sum,min_val,max_val);

        if(sum<0){
            return 0;
        }
        return sum;
    }
};
