/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */

// the idea here is to find the largest bst subtree , then count sum of each element
// and return the sum

// creating class to hold values 
class NodeValue{
public:
    int maxVal , minVal , maxSum , sum , isBst;
    NodeValue( int mx , int mn , int ans , int s , int bst){
        this -> maxVal = mx ;
        this -> minVal = mn ; 
        this -> maxSum = ans ;
        this -> sum = s ;
        this -> isBst = bst;
    }    
};

class Solution {
private:
    NodeValue largeSumBst( TreeNode* root){
        if ( !root) return NodeValue(INT_MIN , INT_MAX , 0 , 0 , 1 );

        // postorder traversal
        auto left = largeSumBst( root -> left );
        auto right = largeSumBst( root -> right );

        // if not bst
        if (!left.isBst or !right.isBst ){
            return NodeValue ( INT_MIN , INT_MAX , max ({ left.maxSum , right.maxSum , 0 }) , root -> val + right.sum + left.sum , 0 );
        }

        // if the tree is a valid bst 
        if ( left.maxVal < root -> val and right.minVal > root -> val){
            return NodeValue ( max ( {root -> val , left.maxVal , right.maxVal }) , 
                             min ({ root -> val , left.minVal , right.minVal }) ,
                             max ({ 0 , (root -> val + left.sum + right.sum ) , right.maxSum , left.maxSum}),
                                root -> val + left.sum + right.sum , 1);
        }

        // not a bst
        return NodeValue ( INT_MIN , INT_MAX , max ({ left.maxSum , right.maxSum , 0}) , root -> val + right.sum + left.sum , 0 );
    }

public:
    int maxSumBST(TreeNode* root) {
        if (!root) return 0 ;
        return largeSumBst( root ).maxSum ;
    }
};
