    int ans = 0;
    Node getSum(TreeNode * root) {
	// base condition
        if (root == NULL) return Node();

        Node node;

		// never question this step, just write it .This is important in every binary tree question
        Node node_left = getSum(root -> left);
        Node node_right = getSum(root -> right);

// induction step, contains the main logic
        if (node_left.isBst == true && node_right.isBst == true && node_left.maximum < root -> val && node_right.minimum > root -> val) {
            node.sum = node_left.sum + node_right.sum + root -> val;
            node.isBst = true;
            node.minimum = min(root -> val, min(node_left.minimum, node_right.minimum));
            node.maximum = max(root -> val, max(node_left.maximum, node_right.maximum));
        } else {
            node.isBst = false;
            node.sum = 0;
        }
        ans = max(ans, node.sum);
        return node;
    }

    int maxSumBST(TreeNode * root) {
        ans = INT_MIN;
        getSum(root);
        if (ans > 0) return ans;
        return 0;
    }
};
