/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
//check if valid BST for given root.
    bool isValid(TreeNode* root, int* left, int* right)
    {
        if(root == NULL)
            return true;
        else
        {
            if(left != NULL && root->val <= *left)
                return false;
            if(right != NULL && root->val >= *right)
                return false;
            return isValid(root->left, left, &root->val) && isValid(root->right, &root->val, right);
        }
    }

    int maxSum(TreeNode* root, int& max)
    {
        if(root == NULL)
            return 0;
        else
        {
            int l = maxSum(root->left, max);
            int r = maxSum(root->right, max);
            max = std::max(max, std::max(l, std::max(r, l + r + root->val)));
            return l + r + root->val;
        }
    }
    //check for valid BST in binary tree
    int getMax(TreeNode* root)
    {
        if(root == NULL)
            return 0;
        if(isValid(root, NULL, NULL))
        {
			// true then even the subtree is BST, so just find the max subtree sum.
            int max = INT_MIN;
            maxSum(root, max);
            return max;
        }
        else
			// Recursive=ly find for left and right nodes, if the present node is not a valid BST.
            return max(getMax(root->left), getMax(root->right));
    }

    int maxSumBST(TreeNode* root) {
            return getMax(root);
    }
};
