//Approach - Finding a bst and calculating its sum (In Bottom Up Manner or In PostOrder)
//At each node i will maintain a minimum value and a maximum value from that node to bottom of tree
//At each node i will check
//  1 - If its left subtree is a BST
//  2 - If its right sub tree is a BST
//  3 - If its value is greater than max of its left (b/c if it is greater than max of its left means it is greater than all the nodes of its left subtree)
//  4 - If its value is less than min of its right b/c if it is less than minimum of its right subtree means it is less than all the nodes of its right subtree
//  5 - if all the above condition satisfies means it is a BST then sum of this BST will be left.sum + right.sum + node.val

class NodeVal{      
    public:
    int minVal, maxVal,sum;
    bool isBST;

    NodeVal(int a, int b, int c, bool e)
    {
        minVal = a;
        maxVal = b;
        sum = c;
        isBST = e;
    }
    NodeVal(){}
};

class Solution {
public:
    int ans = INT_MIN;
    NodeVal findSum(TreeNode* node)
    {
        if(node == NULL) return NodeVal(INT_MAX, INT_MIN, 0, true);

        auto left = findSum(node->left);
        auto right = findSum(node->right);

        NodeVal nv;

        nv.isBST = (left.isBST && right.isBST && node->val < right.minVal && node->val > left.maxVal);
        nv.minVal = min(node->val, left.minVal);
        nv.maxVal = max(node->val, right.maxVal);


        if(nv.isBST)
        {
            nv.sum = left.sum + right.sum + node->val;
            ans = max(ans, nv.sum);
        }

        return nv;
    }

    int maxSumBST(TreeNode* root) {
        auto a = findSum(root);
        return ans > 0 ? ans : 0;
    }
};
