class Solution {
    private int ans = 0;

    public int maxSumBST(TreeNode root) {
        if(root == null) return 0;

        Info info = find(root);
        return ans;
    }

    private Info find(TreeNode root){
        if(root == null){
            return new Info();
        }

        Info lTree = find(root.left);
        Info rTree = find(root.right);

        Info info = new Info();
        if(lTree.isBST == true && rTree.isBST == true){
