class Solution {
    int max=Integer.MIN_VALUE;

    public int[] helper(TreeNode root){
        //return pattern is { max_sum , 0 if not bst and 1 if bst , maximum on left of subtree , minimum on right of subtree}
        if(root==null) return new int[]{0,1,-99999,99999};

        //solve for left and right
        int[] l=helper(root.left);
        int[] r=helper(root.right);

        //maxLeft
        int maxL=Math.max(root.val,Math.max(l[2],r[2]));

        //minRight
        int minR=Math.min(root.val,Math.min(l[3],r[3]));

        // if the subtree is a BST
        if(root.val>l[2]&&root.val<r[3]&&l[1]==1&&r[1]==1){
            max=Math.max(max,l[0]+r[0]+root.val);
            return new int[]{l[0]+r[0]+root.val,1,maxL,minR};
        }

        //if subtree is not BST
        else return new int[]{0,0,maxL,minR};

    }
    public int maxSumBST(TreeNode root) {
        helper(root);
        if(max<0) return 0;
        return max;
    }
}
