/**
 * Definition for a binary tree node.
 * public class TreeNode {
 *     int val;
 *     TreeNode left;
 *     TreeNode right;
 *     TreeNode(int x) { val = x; }
 * }
 */
class Solution {

    class Node {
        int min;
        int max;
        int sum;
        boolean isBST;
    }

    int max = 0;
    public int maxSumBST(TreeNode root) {
        visit(root);
        return max;
    }
    private Node visit(TreeNode root) {
        if (root == null) {
            Node node = new Node();
            node.min = Integer.MAX_VALUE;
            node.max = Integer.MIN_VALUE;
            node.isBST = true;
            return node;
        }
        Node left = visit(root.left);
        Node right = visit(root.right);
        if (!left.isBST || !right.isBST || left.max >= root.val || right.min <= root.val) {
            Node node = new Node();
            node.min = Math.min(root.val,left.min);
            node.max = Math.max(root.val, right.max);
            node.isBST = false;
          return node;   
        }
        Node node = new Node();
        node.min = Math.min(root.val,left.min);
        node.max = Math.max(root.val, right.max);
        node.sum = root.val + left.sum + right.sum;
        max = Math.max(node.sum,max);
        node.isBST = true;
        return node;
    } 
}
