class Solution {
    public int maxSumBST(TreeNode root) {
        return postorder(root)[4];
    }

    private int[] postorder(TreeNode node) {
        if (node == null) {
            return new int[]{1, 0, Integer.MAX_VALUE, Integer.MIN_VALUE, 0};
        }
        int[] ll = postorder(node.left);
        int[] rr = postorder(node.right);
        int local_max = Math.max(ll[4], rr[4]);

        if (ll[0] == 1 && rr[0] == 1 && node.val > ll[3] && node.val < rr[2]) { // it is a BST
            int sum = node.val + ll[1] + rr[1];
            return new int[]{1, sum, ll[2] == Integer.MAX_VALUE ? node.val : ll[2], rr[3] == Integer.MIN_VALUE ? node.val : rr[3], Math.max(sum, local_max)};
        } else {
            return new int[]{0, 0, 0, 0, local_max};
        }
    }
}
