# Code
```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:

    //create struct to store info about left and right subtree
    struct subtree{
        bool isBST; //check if subtree is a BST
        int minn; //minimum value in subtree
        int maxx; //maximum value in subtree
        int sum; //sum of all values in subtree
    };

    struct subtree traverse(TreeNode* root, int &ans){
        if(!root) return {true, INT_MAX, INT_MIN, 0};
        //empty tree is a valid BST with sum 0 hence true
        //initialise min and max to +inf and -inf respectively

        subtree lst = traverse(root->left, ans); //recurse left
        subtree rst = traverse(root->right, ans); //recurse right

        //get information about current node
        bool flag = true; 
        int minval = min({root->val, lst.minn, rst.minn});
        int maxval = max({root->val, lst.maxx, rst.maxx});
        int subtreeSum = root->val + lst.sum + rst.sum;

    //for BST root is greater than all elements in left subtree
                //and smaller than all elements in right subtree
        if(root->val <= lst.maxx || root->val >= rst.minn)
            flag = false;

        //if we are at valid subtree then update ans
        if(flag && lst.isBST && rst.isBST)
            ans = max(ans, subtreeSum);

        return {flag, minval, maxval, subtreeSum};
    }

    int maxSumBST(TreeNode* root) {
        int ans = 0;
        traverse(root, ans);
        return ans;

    }
};
