public:
    int ans=0;
    //returns {sumOfBST,minElementOfBST,maxElementOfBST,isBST}
    vector<int> dfs(TreeNode* root){
        if(!root) // if nullptr
            return {0,INT_MAX,INT_MIN,true}; 
        vector<int>left=dfs(root->left); 
        vector<int>right=dfs(root->right);

        // conditions for the current subtree to be BST
        // 1. if left and right are both BST
        // 2. the maximum value of left subtree is less than node value
        // 3. the minimum value of right subtree is greater than the node value
        if(left[3] && right[3] && left[2]<root->val && right[1]>root->val){
            int sum=left[0]+right[0]+root->val;
            ans=max(sum,ans);
            // if left or right subtree is nullptr, then use current node's value
            return {sum,left[1]==INT_MAX?root->val:left[1],right[2]==INT_MIN?root->val:right[2],true};
        }
        return {0,INT_MIN,INT_MAX,false};
    }
    int maxSumBST(TreeNode* root) {
        dfs(root);
        return ans;
    }
};
