 class Solution {
    int maxSum = 0;
    class Pair{
        int min;
        int max; 
        int sum;
    }

    public int maxSumBST(TreeNode root) {
       Pair sol =  utilFunction(root);
        return maxSum;
    }

    public Pair utilFunction(TreeNode root){
        if(root == null){
           Pair bp = new Pair();
            bp.min = Integer.MAX_VALUE;
            bp.max = Integer.MIN_VALUE;
            bp.sum = 0;

           return bp;

        }

        Pair lp = utilFunction(root.left);
        Pair rp = utilFunction(root.right);
        Pair mp = new Pair();
        if(root.val > lp.max && root.val < rp.min){ //valid bst
            mp.min = Math.min(lp.min , root.val);
            mp.max = Math.max(rp.max , root.val);
            mp.sum = lp.sum + rp.sum + root.val;
            maxSum = Math.max(maxSum , mp.sum);
        }
        else{ //not a valid bst
            mp.min = Integer.MIN_VALUE;
            mp.max = Integer.MAX_VALUE;
            mp.sum = Math.max(lp.sum , rp.sum);
        }
        return mp;
    }


}
