/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */

class Info {
public:
    int minimum;
    int maximum;
    bool isBST;
    int runningSum;
    int maxSum;
};

class Solution {
public:
    Info helper(TreeNode* root) {
        Info inf;
        if (!root) {
            inf.minimum = INT_MAX;
            inf.maximum = INT_MIN;
            inf.isBST = true;
            inf.runningSum = 0;
            inf.maxSum = 0;
            return inf;
        }

        Info left = helper(root->left);
        Info right = helper(root->right);

        inf.minimum = min(root->val, min(left.minimum, right.minimum));
        inf.maximum = max(root->val, max(left.maximum, right.maximum));
        inf.isBST = left.isBST && right.isBST && (root->val > left.maximum) && (root->val < right.minimum);
        inf.runningSum = left.runningSum + right.runningSum + root->val;

        if (inf.isBST) {
            inf.maxSum = max(inf.runningSum, max(left.maxSum, right.maxSum));
        }
        else
            inf.maxSum = max(left.maxSum, right.maxSum);

        return inf;
    }
    int maxSumBST(TreeNode* root) {
        return helper(root).maxSum;
    }
};
