class Solution {
    static class Pair{
        int max;
        int min;
        int sum;
        boolean isBst;
        Pair(int l,int r,int m,boolean b){
            this.max=l;
            this.min=r;
            this.sum=m;
            this.isBst=b;

        }
		//1 Every Node will return the above parameters above .
		//2 If the root value is  larger than max left value the it will be larger than all left values .
	//	3 If the root value is less than min value of right that it will be less than all other values hence will form a bst and we are also tracking sum.
    }

    static Pair recur(TreeNode r,int a[]){
        if(r==null){
            return new Pair(Integer.MIN_VALUE,Integer.MAX_VALUE,0,true);
        }
        Pair lobj=recur(r.left,a);
        Pair robj=recur(r.right,a);
        if(r.val<=lobj.max||r.val>=robj.min)
        return new Pair(Integer.MIN_VALUE,Integer.MAX_VALUE,0,false);
         int maxLeft=Math.max(r.val,Math.max(lobj.max,robj.max));
         int minRight=Math.min(r.val,Math.min(lobj.min,robj.min));
        boolean chk=false;
         if(lobj.isBst&&robj.isBst){
             a[0]=Math.max(a[0],lobj.sum+robj.sum+r.val);
             chk=true;   
         }
        else if(lobj.isBst){
            a[0]=Math.max(a[0],lobj.sum);
            chk=false;
        }
        else if(robj.isBst){
            a[0]=Math.max(a[0],robj.sum);
            chk=false;
        }
        return new Pair(maxLeft,minRight,lobj.sum+robj.sum+r.val,chk);

    }
     public int maxSumBST(TreeNode root) {
         int a[]=new int[1];
          recur(root,a);
        return a[0];
    }
}

