/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int ans=0;
    pair<int,pair<int,int>>solve(TreeNode*root,int mini,int maxi,int sum)
    {
		//if root is null then it must be bst as it is a single node
        if(!root)
        {
            pair<int,pair<int,int>>curr;
            curr.first=0;
            curr.second.first=INT_MAX;
            curr.second.second=INT_MIN;
            return curr;
        }
        auto l=solve(root->left,mini,maxi,sum);
        auto r=solve(root->right,mini,maxi,sum);

        pair<int,pair<int,int>>curr;
		//if left and right are bst
        if(root->val>l.second.second && root->val<r.second.first)
        {
		//sum will be root->val+left +right
            curr.first=l.first+r.first+root->val;
		//min will be root,left,right
            curr.second.first=min(r.second.first,min(root->val,l.second.first));
		//max will be root,left,right
            curr.second.second=max(l.second.second,max(root->val,r.second.second));
		//store max ans;
            ans=max(ans,curr.first);
            return curr;
        }
		//if left and right are not bst
        curr.first=0;
		//initialise with int min and int max so that it will always false when comparing
        curr.second.first=INT_MIN;
        curr.second.second=INT_MAX;
        return curr;
    }
    int maxSumBST(TreeNode* root) {
        solve(root,INT_MIN,INT_MAX,0);
        return ans;
    }
