```
/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    struct bst{
         bool isbst;
      int max;
      int min;
      int sum;
    };

    bst Bst(TreeNode* root)
    {
        //we are at end of tree where node->left or node->right==nullprt
        //if we find a nullptr, we initialize a newbst to compare it with nodes above
       if(root==nullptr)
       {
        bst newbst;           
        newbst.isbst = true;
        newbst.max = INT_MIN;
        newbst.min = INT_MAX;
        newbst.sum = 0;
        return newbst;
       }

       //postorder traversal
      bst l = Bst(root->left); // left sub-tree
      bst r = Bst(root->right); // right sub-tree

        //our current node to check
        bst currnode;

        //check if its a bst

        //conditions->its left and right subrees must be a BST
        //->current node value > than max value of left subtree and <min value in right subtree
        currnode.isbst= l.isbst && r.isbst && (l.max < root->val && r.min > root->val);

        //find the max value of the subtree -> max of(max of left subtree,max of right subtree,root)
      currnode.max = max(root->val, max(l.max, r.max));

        //find the min value of the subtree -> min of(min of left subtree,min of right subtree,root)
      currnode.min = min(root->val, min(l.min, r.min));

        //we are keeping track of min and max value to finally check if currnode is bst or not from whole of its left and right subtree max and min respectively and for finding max and min value of whole subtree to use it for its parent.

        //if currnode is BST, we need to store its sum along with its min and max value from left and right subtree
        if(currnode.isbst){
          currnode.sum = l.sum + r.sum + root->val;
          currnode.min = min(root->val, min(l.min, r.min));
          currnode.max = max(root->val, max(l.max, r.max));
      }

        //if not a BST , then see in left subtree which stores max sum of BST in l.sum and right subtree which stores max sum of BST in r.sum and update current sum with that.
      else
          currnode.sum = max(l.sum, r.sum);

     //now my currnode , whether or not its a BST, contains the sum of BST ,either be it from its left subtree or its right subtree

        //check if its greater than max sum and store the ans
      res = max(res, currnode.sum);

      return currnode;

    }
    int res = INT_MIN;
    int maxSumBST(TreeNode* root) {
        Bst(root);
        return res > 0 ? res : 0;
    }
};
```

