    public Pair findMax(TreeNode root) {
        if (root == null) {
            return new Pair(true, Integer.MAX_VALUE, Integer.MIN_VALUE, 0);
        }
        Pair myPair = new Pair();
        Pair l = findMax(root.left);
        Pair r = findMax(root.right);
