class Solution {
public:
    //this struct will contain subtree's min and max value , sum of left and right subtrees ,
	//ans that conatins sum of nodes of largest bst formed yet 
	//and isBst conatins boolean value that whether the subtree is bst or not
    struct Info{
        int min;
        int max;
        int sum;
        int ans;
        bool isBst;
    };

    Info sol(TreeNode* root){
        if(!root)return {INT_MAX, INT_MIN,0,INT_MIN,true};//if root==NULL
        if(!root->left&&!root->right){
            return {root->val,root->val,root->val,root->val,true};//if leaf node
        }
        //recursive calls for left and right subtree
        Info left= sol(root->left);
        Info right= sol(root->right);


        Info cur; //for current info
        //checks condition whether cur node forms bst or not
        if(left.isBst && right.isBst && root->val>left.max&&root->val < right.min){
            cur.min= min(left.min,min(right.min,root->val));//for bst validation purpose
            cur.max= max(left.max, max(right.max,root->val));//for bst validation purpose

            cur.sum=left.sum+right.sum+root->val;//left part + right part+root value

            //either ans will be left.ans or right.ans or cur.sum
            if(left.sum==INT_MIN){//if left subtree is NULL
                cur.ans = max(left.ans,max(right.ans,right.sum+root->val));    
            }
            else if(right.sum==INT_MIN){//ifright subtree is NULL
                cur.ans = max(left.ans,max(right.ans,left.sum+root->val));    
            }
            else{
                cur.ans = max(left.ans,max(right.ans,cur.sum));    
            }

            cur.isBst=true;
            return cur;
        }
        cur.sum=left.sum+right.sum+root->val;//cur.sum is of no use now
        cur.ans= max(left.ans, right.ans);//only check max from left.ans and right.ans
        cur.isBst= false;//set bst to false
        return cur;

    }
    int maxSumBST(TreeNode* root) {
        Info a= sol(root);
        if(a.ans<0)return 0;
        return a.ans;
    }
};
