 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    int maximumBSTSum = 0;

    bool isBST(TreeNode* root, int &minVal, int &maxVal, int &currentSum) {
        if(!root)return true;

        int leftSum = 0;
        int rightSum = 0;
        int minLeft = INT_MIN;
        int maxLeft = INT_MAX;
        int minRight = INT_MIN;
        int maxRight = INT_MAX;
        auto leftHalf = isBST(root->left, minLeft, maxLeft, leftSum);
        auto rightHalf = isBST(root->right, minRight, maxRight, rightSum);

        //cout<<minLeft<<" "<<maxLeft<<" maxLR "<<minRight<<" "<<maxRight<<endl;
        if(!root->left && !root->right){
            minVal = root->val;
            maxVal = root->val;
        }else if(!root->left) {
            if(minRight <= root->val)return false;
            minVal = root->val;
            maxVal = maxRight;
        }else if(!root->right){
            if(maxLeft >= root->val)return false;
            minVal = minLeft;
            maxVal = root->val;
        }else {
            if(minRight <= root->val || maxLeft >= root->val)return false;
            minVal = minLeft;
            maxVal = maxRight;
        }

        if(leftHalf&&rightHalf){
            currentSum += root->val+leftSum+rightSum;
        } else currentSum = 0;

        maximumBSTSum = max(maximumBSTSum, currentSum);

        return leftHalf && rightHalf;
    }

