class Solution:
    def maxSumBST(self, root: Optional[TreeNode]) -> int:

        def check(root):
            def validate(root,low=-math.inf,high=math.inf):
                if not root:
                    return
                if not low<root.val<high:
                    self.bst=False
                self.sm+=root.val
                validate(root.left,low,root.val)
                validate(root.right,root.val,high)

            self.bst=True
            self.sm=0
            validate(root)
            if self.bst==True:
                return self.sm
            return 0

        def dfs(root):
            if not root:
                return
            self.ans=max(self.ans,check(root))
            dfs(root.left)
            dfs(root.right)

        self.ans=0
        dfs(root)
        return self.ans
