            mp.isBST = true;

            int tempMax = Math.max(mp.sum, Math.max(lp.sum, rp.sum));
            mp.maxSum = Math.max(tempMax, Math.max(lp.maxSum, rp.maxSum));

        } else {
            mp.isBST = false;
            mp.maxSum = Math.max(lp.maxSum, rp.maxSum);
        }
        return mp;
