 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Sol{

   public:
    int mn, mx, sum;
    Sol(int mn_, int mx_, int sum_){
        mn = mn_;
        mx = mx_;
        sum = sum_;
    }
};

class Solution {
public:
    int ans = INT_MIN;

    Sol findAns(TreeNode * root){
       if(root==NULL) {
           return Sol(INT_MAX, INT_MIN, 0);
       }
       int x = root->val;
       Sol l = findAns(root->left);
        Sol r = findAns(root->right);
        if(l.mx < x and x< r.mn){
            if(root->left!=NULL and root->right!=NULL){ // has both children
                ans = max(ans, l.sum+r.sum+x);
                return Sol(l.mn, r.mx, l.sum+r.sum+x);
            }
            else if(root->left!=NULL){ // has only left child
               ans = max(ans, l.sum+x);
                return Sol(l.mn, x, l.sum+x);
            }
            else if(root->right!=NULL){ // has only right child
               ans = max(ans, r.sum+x) ;
                return Sol(x, r.mx, r.sum+x);
            }
            else{ // no children present
                ans = max(ans, x);
                return Sol(x, x, x);
            }
        }
        else{
            return Sol(INT_MIN, INT_MAX, 0);
        }
    }

    int maxSumBST(TreeNode* root) {
        findAns(root);
        return max(0, ans);
    }
};
