class Solution {
    func generateTheString(_ n: Int) -> String {
        var outputString = String()
        if n % 2 == 0 {
            outputString = repeatCharacter(char: "c", count: n - 1)
            outputString.append("x")
            return outputString
        }
        outputString = repeatCharacter(char: "c", count: n)
        return outputString
    }

    // Time complexity O(logn)
    func repeatCharacter(char: Character, count targetCount: Int) -> String {
        var outputString = String(char)

        // This is a computed property which gives you the 
        // up to date value everytime its accessed
        var count: Int {
            return outputString.count
        }

        while count < targetCount {
            // If appending the string to itself will cause the count to exceed the desired count,
            // we find the num of char required to complete the output by finding the difference
            // and take a substring and append to itself
            if (count * 2) > targetCount {
                let startIndex = outputString.startIndex
                let endIndex = outputString.index(outputString.startIndex, offsetBy: targetCount - count)
                outputString.append(String(outputString[startIndex..<endIndex]))
            } else {
                // Append the string to itself
                outputString.append(outputString)
            }
        }
        return outputString
    }
}
