class Solution {
    func minDeletionSize(_ strs: [String]) -> Int {
        strs.reduce(Array(repeating:"a", count:strs[0].count)) { columns, str in
			// Approach:
			// - Replace column element with `~` where lexographic order not maintained.
			// - At the end, return count of `~` in `columns`
            zip(columns, str)
            .map {
                // $0.0 is `columns` element 
                // $0.1 is `str` element
                $0.0 > $0.1 ? "~" : $0.1  // `~` is (arbitrarily-chosen) larger ASCII value than `z`
            }
        }
        .filter { ch in ch == "~" }
        .count
    }
}
```

