        while(c < vec[0].size()) {
            if(r == vec.size()-1){
                r = 0;
                c++;
            }
            else if(vec[r][c] > vec[r + 1][c]) {
                colDel++;
                c++;
                r = 0;
            }
            else r++;
        }
        return colDel;
    }
};
