public TreeNode balanceBST(TreeNode root) {
  TreeNode grand = new TreeNode(0);
  grand.right = root;
  int cnt = makeVine(grand);
  int m = (int)Math.pow(2, (int)(Math.log(cnt + 1) / Math.log(2))) - 1;
  compress(grand, cnt - m);
