    // we choose the middle value as the root, so that the difference between right and left height would be at most 1
    TreeNode* generateBST(vector<TreeNode*>& res, int begin, int end) {
        if (begin > end) {
            return NULL;
        }
        int mid = (begin + end) / 2;
        TreeNode* root = res[mid];
        root->left = generateBST(res, begin, mid - 1);
        root->right = generateBST(res, mid + 1, end);
        return root;
