TreeNode* balanceBST(TreeNode* root) {
        auto* node = root;
        int n = 0;
        stack<TreeNode*> sk;
        TreeNode* prev = nullptr, *head = nullptr;
        while (!sk.empty() || node) {
            while (node) {
                sk.push(node);
                node = node->left;
            }
            node = sk.top();
            sk.pop();
            if (prev) {
                prev->right = node;
            } else {
                head = node;
            }
            n++;
            node->left = nullptr;
            prev = node;
            node = node->right;
        }
        return helper(head, n);
    }

    TreeNode* helper(TreeNode*& node, int n) {
        if (n == 0) return nullptr;        
        node->left = helper(node, n/2);
        auto* root = node;
        node = node->right;
        root->right = helper(node, n-n/2-1);
        return root;
    }
