function balanceBST(root) {

    function rightRotate(node) {
        const mid = node.left;
        node.left = mid.right;
        mid.right = node;
        return mid;
    }

    function leftRotate(node) {
        const mid = node.right;
        node.right = mid.left;
        mid.left = node;
        return mid;
    }

    function getHeight(node) {
        if(!node) return 0
        return Math.max(getHeight(node.left), getHeight(node.right)) + 1
    }

    function recurse(node) {
        if(!node) return null;

        node.left = recurse(node.left);
        node.right = recurse(node.right);

        const leftHeight = getHeight(node.left);
        const rightHeight = getHeight(node.right);

        if(Math.abs(leftHeight - rightHeight) <= 1) return node

        if(leftHeight > rightHeight) {

            const leftOfLeft = getHeight(node.left.left);
            const rightOfLeft = getHeight(node.left.right);

            if(rightOfLeft > leftOfLeft) {
                node.left = leftRotate(node.left);
            }
            node = rightRotate(node);
        } else if(rightHeight > leftHeight) {

            const leftOfRight = getHeight(node.right.left);
            const rightOfRight = getHeight(node.right.right);

            if(leftOfRight > rightOfRight) {
                node.right = rightRotate(node.right);
            }
            node = leftRotate(node);
        }
        return recurse(node);
    }

    return recurse(root)
};
