class Solution {

	private void inorderTraversal (TreeNode root, List<Integer> inorderList) {

		if (root != null) {
			inorderTraversal (root.left, inorderList);
			inorderList.add (root.val);
			inorderTraversal (root.right, inorderList);
		}
	}

	private TreeNode constructBinarySearchTree (List<Integer> inorderList, int start, int end) {

		if (start > end) {
			return null;
		}

		int mid = (start + end) / 2;
		TreeNode root = new TreeNode (inorderList.get (mid));

		root.left = constructBinarySearchTree (inorderList, start, mid - 1);
		root.right = constructBinarySearchTree (inorderList, mid + 1, end);

		return root;
	}

	public TreeNode balanceBST (TreeNode root) {

		if (root == null) {
			return root;
		}

		List<Integer> inorderList = new ArrayList <>();
		inorderTraversal (root, inorderList);

		return constructBinarySearchTree (inorderList, 0, inorderList.size () - 1);
	}
}

