- inorder a BST can retrieve a sorted array.
- rebuild the BST and make sure the middle of the sorted arry is the root. this can help the tree is always balanced.
- recusion.
```
public class Solution {
    List<TreeNode> list = new List<TreeNode>();

    public TreeNode BalanceBST(TreeNode root) {
        if (root == null || root.left == null && root.right == null)
            return root;

        DFS(root);

        return Rebuild(0, list.Count - 1);
    }

    private void DFS(TreeNode node)
    {
        if (node == null)
            return;

        DFS(node.left);
        list.Add(node);
        DFS(node.right);
    }

    private TreeNode Rebuild(int i, int j)
    {
        if (i > j)
            return null;

        TreeNode root = new TreeNode();

        if (i == j)
            root.val = list[i].val;
        else
        {
            int mid = j + (i - j) / 2;

            root.val = list[mid].val;
            root.left = Rebuild(i, mid - 1);
            root.right = Rebuild(mid + 1, j);
        }

        return root;
    }
}

